import React, { useState } from "react";
import "./compounding.css";
import Dermatology from "./images/dermatology.png";
import HairLoss from "./images/hairLoss.jpeg";
import Dental from "./images/dental.jpeg";
import HormoneTherapy from "./images/hormoneTherapy.jpg";
import PainManagement from "./images/painManagement.jpeg";
import Veterinary from "./images/veterinary.jpeg";

const Compounding = () => {
  const [selectedProduct, setSelectedProduct] = useState<string | null>(null);

  const openPopup = (product: string) => {
    setSelectedProduct(product);
  };

  const closePopup = () => {
    setSelectedProduct(null);
  };

  const getPopupContent = () => {
    switch (selectedProduct) {
      case "Dermatology":
        return (
          <div>
            <h2>Dermatology</h2>
            <ul>
              <li>Aesthetic Treatments: Enhance skin appearance.</li>
              <li>Lightening Agents: Reduce hyperpigmentation.</li>
              <li>Chemical Peels: Exfoliate skin for improved texture.</li>
              <li>Acne Treatments: Topical and oral solutions.</li>
              <li>Wart Treatments: Targeting conditions like keratosis.</li>
            </ul>
          </div>
        );
      // other cases go here...
      default:
        return null;
    }
  };

  return (
    <div className="compounding-container">
      <div className="title">Compounding</div>
      <div className="additional-info">
        <p>
          For detailed information on specific medications, treatments, and
          protocols, you can sign up for access.
        </p>
      </div>
      <div className="product-grid">
        <div className="product-item" onClick={() => openPopup("Dermatology")}>
          <img src={Dermatology} alt="Dermatology" />
          <p>Dermatology</p>
          <button>LEARN MORE</button>
        </div>
        {/* other products */}
      </div>

      {selectedProduct && (
        <div className="popup-overlay" onClick={closePopup}>
          <div className="popup" onClick={(e) => e.stopPropagation()}>
            {getPopupContent()}
            <button onClick={closePopup}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Compounding;
