import React, { useState } from "react";
import "./compounding.css";

const Compounding: React.FC = () => {
  const [selectedProduct, setSelectedProduct] = useState<string | null>(null);

  const products = [
    { name: "Dentistry", description: "Description about Dentistry product." },
    { name: "Dermatology", description: "Description about Dermatology product." },
    { name: "Pain Management", description: "Description about Pain Management product." },
  ];

  const handleLearnMore = (productName: string) => {
    setSelectedProduct(productName);
  };

  const closePopup = () => {
    setSelectedProduct(null);
  };

  return (
    <div className="compounding-container">
      <div className="title">Compounding</div>
      <div className="products">
        {products.map((product) => (
          <div key={product.name} className="product-item">
            <img src={`/${product.name.toLowerCase()}.png`} alt={product.name} />
            <h3>{product.name}</h3>
            <button onClick={() => handleLearnMore(product.name)}>Learn More</button>
          </div>
        ))}
      </div>

      {selectedProduct && (
        <div className="popup-overlay" onClick={closePopup}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <h2>{selectedProduct}</h2>
            <p>{products.find((p) => p.name === selectedProduct)?.description}</p>
            <button className="close-button" onClick={closePopup}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Compounding;
